package br.com.generation.aula01.exemplovariavel;

public class ExemploVariavel03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int variavel1;
		double variavel2;
		
		variavel1 = 10;
		variavel2= 10.0;
		
		System.out.println("Valor da 1° variavel: " + variavel1);
		System.out.println("Valor da 2° variavel: " + variavel2);
		
		variavel1 = variavel1 / 4;

		variavel2 = variavel2 / 4;

		System.out.println("Valor da variavel após divisão: " + variavel1);
		System.out.println("Valor da variavel após divisão: " + variavel2);


	
	//fim programa
	}

}
